
use candi;
create table worldc(
city varchar(50),
country varchar(50),
continent varchar(50),
latitude float(50),
Northsouth varchar(50),
longitude float(50),
Eastwest varchar(50));
select * from worldc;
select continent,country from worldc order by country;
SELECT DISTINCT Country FROM worldc;
select city,country from worldc WHERE COUNTRY LIKE 'R%'